import React, { useEffect, useState, useRef } from "react";
import { fetchData } from "../../utils/fetchData";

import Loader from "../../components/UI/Loader/Loader";

import classes from "../Page.module.css";
// import MyTable from "../../components/UI/Table/MyTable";
import MyButton from "../../components/UI/Button/MyButton";
import BasicTable from "../../components/UI/Table/BasicTable";
import MySubmitButton from "../../components/UI/SubmitButton/MySubmitButton";
import { COLUMNS } from "../../components/UI/Table/Columns";

const Assets = () => {
	const tab = window.location.pathname.split("/assets/")[1];
	const [data, setData] = useState(null);
	const [values, setValues] = useState(null);

	useEffect(() => {
		fetchData("assets", "get", null, setData);
	}, []);

	const fileInputRef = useRef(null);

	const handleButtonClick = () => {
        fileInputRef.current.click(); // Программно вызываем выбор файла
    };

	const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            console.log("Выбран файл:", selectedFile.name);
			fetchData(`assets/import?name=${tab}&file=${selectedFile}`, "post", null, null, (error) => console.error("Ошибка запроса:", error), null, true)
        }
    };

	const handleChange = (event, field) => {
		setValues((prevValues) => ({
			...prevValues,
			[field]: event.target.value,
		}));
	};

	const handleDownload = () => {
		fetchData(`assets/export?name=${tab}`, "GET", null, null, (error) => console.error("Ошибка запроса:", error), null, true, "example_model.csv");
	};

	const tableRef = useRef();

	let [isVisible, setVisible] = useState(false);

	useEffect(() => {
		setVisible(false);
	  }, [tab]);

	const toggleVisible = () => {
		setVisible(!isVisible);
	}

	let buttonText = isVisible ? "Отмена" : "Создать запись";

	const [file, setFile] = useState(null);
    const [response, setResponse] = useState(null);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleFileChangeZ = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            setError("Выберите файл перед загрузкой.");
            return;
        }

        const formData = new FormData();
        formData.append("name", "Equipments");
        formData.append("file", file);

        await fetchData(
            "assets/import", 
            "POST", 
            formData, 
            setResponse, 
            setError, 
            setSuccess
        );
    };

	return (
		<div className={classes.main}>
			{data ? (
				<>
					<BasicTable columnlist={COLUMNS} ref={tableRef} fulldata={data[tab]} />
					<div style={{ display: 'flex' }}>
						<MyButton
						style={{ marginTop: '20px', marginRight: '20px', width: '250px' }}
						onClick={() => {
							toggleVisible();
							if (!isVisible) {
								tableRef.current?.addRow();
								toggleVisible();
							} else {
								tableRef.current.cancelEdit();
							}
						}}
						text={buttonText}
						/>
						<MySubmitButton isActive={isVisible} style={{ marginTop: '20px', marginRight: '20px', width: '250px'}} text="Подтвердить" 
						onClick={() => {
							tableRef.current.sendData();
							toggleVisible();
						}}
						/>
						<input
							type="file"
							ref={fileInputRef}
							style={{ display: 'none' }} // Скрываем input
							onChange={handleFileChange}
            			/>
						<MyButton
						style={{ marginTop: '20px', marginLeft: 'auto', width: '250px' }}
						onClick={handleButtonClick}
						isActive={false}
						text={'Импорт'}
						/>
						<MyButton
						style={{ marginTop: '20px', marginLeft: '20px', width: '250px' }}
						onClick={handleDownload}
						isActive={true}
						text={'Экспорт'}
						/>
						<div>
							<input type="file" onChange={handleFileChangeZ} />
							<button onClick={handleUpload}>Отправить файл</button>
							{error && <p style={{ color: "red" }}>{error}</p>}
							{success && <p style={{ color: "green" }}>{success}</p>}
						</div>
					</div>
				</>
			) : (
				<div style={{ margin: "auto" }}>
					<Loader />
				</div>
			)}
			{/* {data ? keys.map((key) => <div key={key}>{key}</div>) : <Loader />} */}
		</div>
	);
};

export default Assets;
