import React, { useEffect, useState, useRef } from "react";
import { fetchData } from "../../utils/fetchData";
import Loader from "../../components/UI/Loader/Loader";
import classes from "../Page.module.css";
import MyButton from "../../components/UI/Button/MyButton";
import { BasicTable } from "../../components/UI/Table/BasicTable";
import MySubmitButton from "../../components/UI/SubmitButton/MySubmitButton";
import { COLUMNS } from "../../components/UI/Table/Columns";

const Assets = () => {
    const tab = window.location.pathname.split("/assets/")[1];
    const [data, setData] = useState(null);
    const [values, setValues] = useState();
    const [isVisible, setVisible] = useState(false);
    const [isEditingWithPencil, setIsEditingWithPencil] = useState(false); // Используем состояние вместо useRef
    const tableRef = useRef(null);
    const fileInputRef = useRef(null);

    useEffect(() => {
        setData(null);
        fetchData("assets", "get", null, (fetchedData) => {
            setData(fetchedData);
        });
    }, [tab]);

    const handleButtonClick = () => {
        fileInputRef.current.click();
    };

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            const formData = new FormData();
            formData.append("file", selectedFile);
            formData.append("name", tab);

            fetchData(
                `assets/import/`,
                "POST",
                formData,
                () => {
                    fetchData("assets", "get", null, setData);
                },
                setValues,
                setValues
            );
        }
    };

    const handleDownload = () => {
        fetchData(`assets/export?name=${tab}`, "GET", null, null, (error) => console.error("Ошибка запроса:", error), null, true, `${tab}_table.csv`);
    };

    useEffect(() => {
        setVisible(false);
    }, [tab]);

    const toggleVisible = () => {
        setVisible(!isVisible);
    };

    let buttonText = isVisible ? "Отмена" : "Создать запись";

    const handleSave = () => {
        if (tableRef.current) {
            console.log("Calling sendData...");
            tableRef.current.sendData();
        } else {
            console.log("tableRef is null");
        }
    };

    // Следим за изменениями isEditingWithPencil и обновляем isVisible
    useEffect(() => {
        setVisible(isEditingWithPencil);
        // console.log(isEditingWithPencil, isVisible)
    }, [isEditingWithPencil]);

    return (
        <div className={classes.main}>
            {data ? (
                <>
                    <BasicTable
                        key={`${tab}-${values}`}
                        columnlist={COLUMNS}
                        ref={tableRef}
                        fulldata={data[tab]}
                        onEditingChange={(isEditingWithPencil) => {
                            setIsEditingWithPencil(isEditingWithPencil); // Обновляем состояние
                        }}
                    />
                    <div style={{ display: 'flex' }}>
                        <MyButton
                            style={{ marginTop: '20px', marginRight: '20px', width: '250px' }}
                            onClick={() => {
                                // toggleVisible();
                                console.log(`In Button: ${isEditingWithPencil}`)
                                if (!isEditingWithPencil) {
                                    tableRef.current?.addRow();
                                    toggleVisible();
                                } else {
                                    tableRef.current.cancelEdit();
                                }
                            }}
                            text={buttonText}
                        />
                        <MySubmitButton
                            isActive={isVisible}
                            style={{ marginTop: '20px', marginRight: '20px', width: '250px' }}
                            text="Подтвердить" 
                            onClick={() => {
                                tableRef.current.sendData();
                                setVisible(false); // Закрываем кнопку после подтверждения
                            }}
                        />
                        <input
                            type="file"
                            ref={fileInputRef}
                            style={{ display: 'none' }}
                            onChange={handleFileChange}
                        />
                        <MyButton
                            style={{ marginTop: '20px', marginLeft: 'auto', width: '250px' }}
                            onClick={handleButtonClick}
                            isActive={false}
                            text={`Импорт ${values ? "values" : ""}`}
                        />
                        <MyButton
                            style={{ marginTop: '20px', marginLeft: '20px', width: '250px' }}
                            onClick={handleDownload}
                            isActive={true}
                            text={'Экспорт'}
                        />
                    </div>
                </>
            ) : (
                <div style={{ margin: "auto" }}>
                    <Loader />
                </div>
            )}
        </div>
    );
};

export default Assets;
