import React, { useState } from "react";
import { useTable } from 'react-table'

import classes from "./MyTable.module.css";

import '../../../utils/editTables'





const MyTable = ({ fulldata }) => {
  if (!fulldata || fulldata.length === 0) return <p>Нет данных</p>;

	const obj = [
		  {
			Header: 'Id',
			accessor: 'id',
		  },
		  {
			Header: 'Имя',
			accessor: 'first_name',
		  },
	]

  const COLUMNS = Object.keys(obj);
  console.log(COLUMNS)


  return (
    <div className={classes.container}>
          <div className={classes.table}>
            <div className={classes.table__row}>
              {Object.keys(fulldata[0] || {}).map((key, index) => (
                <div key={index} className={`${classes.table__cell} ${classes.table__header}`}>
                  {key}
                </div>
              ))}
            </div>
            {fulldata.map((row, rowIndex) => (
              <div key={rowIndex} className={classes.table__row}>
                {Object.values(row).map((value, index) => (
                  <div key={index} className={classes.table__cell}>
                    {(value &&
                      (Array.isArray(value) ? value.join(", ") : value)) ||
                      "-"}
                  </div>
                ))}
              </div>
            ))}
        </div>
    </div>
  );
};

export default MyTable;
