import React, { useMemo, useState, useEffect, useImperativeHandle, forwardRef } from 'react';
import { useTable } from 'react-table';
import { COLUMNS } from './Columns';
import { fetchData } from '../../../utils/fetchData';
import classes from './MyTable.module.css';

const BasicTable = forwardRef(({ fulldata }, ref) => {
    const currentTab = window.location.pathname.slice(1);
    const [data, setData] = useState(fulldata);
    const [editableRowIndex, setEditableRowIndex] = useState(null);

    const columns = useMemo(() => COLUMNS, []);

    // let [response, setResponse] = useState('');

    // setResponse = (e) => {
    //     response = e;
    // }

    // useEffect(() => {
    //     fetchData("assets/custom", "get", null, setResponse)
    //     if (response) {
    //         console.log(response)
    //     } else {
    //         console.log('Нет данных')
    //     }
    // }, [])

    const tableInstance = useTable({
        columns,
        data,
    });

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

    const addRow = () => {
        const newRow = {
            id: data.length + 1,
        };
        setData([...data, newRow]);
        setEditableRowIndex(data.length);
    };

    const cancelEdit = () => {
        if (editableRowIndex !== null) {
            const lastRow = data[editableRowIndex];
            if (lastRow) {
                const rowData = {};
                columns.forEach(column => {
                    if (column.accessor) {
                        rowData[column.accessor] = lastRow[column.accessor] || '';
                    }
                });
                fetchData(currentTab, "post", rowData, setResponse);
                console.log("Отменена последняя добавленная строка:", rowData);
            }
            setData(prevData => prevData.slice(0, -1));
            setEditableRowIndex(null);
        }
    };

    const handleInputChange = (rowIndex, columnId, value) => {
        if (rowIndex === editableRowIndex) {
            const newData = [...data];
            newData[rowIndex][columnId] = value;
            setData(newData);
        }
    };

    useEffect(() => {
        setData(fulldata);
        setEditableRowIndex(null);
    }, [fulldata]);

    useImperativeHandle(ref, () => ({
        addRow,
        cancelEdit
    }));

    return (
        <div className={classes.container}>
            <table className={classes.table} {...getTableProps()}>
                <thead>
                    {headerGroups.map(headerGroup => {
                        const { key, ...headerGroupProps } = headerGroup.getHeaderGroupProps();
                        return (
                            <tr className={classes.table__row} key={key} {...headerGroupProps}>
                                {headerGroup.headers.map(column => {
                                    const { key, ...columnProps } = column.getHeaderProps();
                                    return (
                                        <th className={classes.table__cell} key={key} {...columnProps}>
                                            {column.render('Header')}
                                        </th>
                                    );
                                })}
                            </tr>
                        );
                    })}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map((row, rowIndex) => {
                        prepareRow(row);
                        const { key, ...rowProps } = row.getRowProps();
                        return (
                            <tr className={classes.table__row} key={key} {...rowProps}>
                                {row.cells.map(cell => {
                                    const { key, ...cellProps } = cell.getCellProps();
                                    return (
                                        <td className={classes.table__cell} key={key} {...cellProps}>
                                            {rowIndex === editableRowIndex && cell.column.id !== 'id' ? (
                                                <input
                                                    className={classes.input}
                                                    value={cell.value || ''}
                                                    onChange={(e) => handleInputChange(rowIndex, cell.column.id, e.target.value)}
                                                    required
                                                />
                                            ) : (
                                                cell.render('Cell')
                                            )}
                                        </td>
                                    );
                                })}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    );
});

export default BasicTable;
