import React, { useState } from "react";
import classes from "./MyTable.module.css";
import '../../../utils/editTables';

const MyTable = ({ fulldata }) => {
  const [tableData, setTableData] = useState(fulldata || []);
  const [editableRowIndex, setEditableRowIndex] = useState(null);

  const addRow = () => {
    const newRow = Object.keys(tableData[0] || {}).reduce((acc, key) => {
      acc[key] = "";
      return acc;
    }, {});
    setTableData([...tableData, newRow]);
    setEditableRowIndex(tableData.length);
  };

  const handleChange = (rowIndex, key, value) => {
    if (rowIndex !== editableRowIndex) return;
    const updatedTable = tableData.map((row, index) =>
      index === rowIndex ? { ...row, [key]: value } : row
    );
    setTableData(updatedTable);
  };

  if (!tableData || tableData.length === 0) return <p>Нет данных</p>;

  return (
    <div className={classes.container}>
      <div className={classes.table}>
        <div className={classes.table__row}>
          {Object.keys(tableData[0] || {}).map((key, index) => (
            <div key={index} className={`${classes.table__cell} ${classes.table__header}`}>
              {key}
            </div>
          ))}
        </div>
        {tableData.map((row, rowIndex) => (
          <div key={rowIndex} className={classes.table__row}>
            {Object.keys(row).map((key, index) => (
              <div key={index} className={classes.table__cell}>
                {rowIndex === editableRowIndex ? (
                  <input
                    type="text"
                    value={row[key] || ""}
                    onChange={(e) => handleChange(rowIndex, key, e.target.value)}
                    className={classes.input}
                  />
                ) : (
                  row[key] || "-"
                )}
              </div>
            ))}
          </div>
        ))}
      </div>
      <button onClick={addRow} className={classes.addButton}>Добавить строку</button>
    </div>
  );
};

export default MyTable;
