import React, { useMemo} from 'react'
import { useTable } from 'react-table'
import {COLUMNS} from './Columns'
import classes from './MyTable.module.css'

function BasicTable({ fulldata }) {
    const columns = COLUMNS
    const data = fulldata

    const tableInstance = useTable({
        columns,
        data
    })

    const { getTableProps, getTableBodyProps, headerGroups, footerGroups, rows, prepareRow} = tableInstance

    const addRow = () => {
        const newRow = {
            id: data.length + 1,
            name: "Новый пользователь",
            age: 20,
        };
        setData([...data, newRow]);
    };

  return (
    <div className={classes.container}>
        <table className={classes.table} {...getTableProps()}>
            <thead>
                {headerGroups.map((headerGroup) => (
                    <tr className={classes.table__row} {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th className={classes.table__cell} {...column.getHeaderProps()}> 
                            {column.render('Header')}
                        </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map(row => {
                    prepareRow(row)
                    return (
                        <tr className={classes.table__row} {...row.getRowProps()}>
                        {row.cells.map((cell) => {
                            return <td className={classes.table__cell} {...cell.getCellProps()}>
                            {cell.render('Cell')}</td>
                        })}
                        </tr>
                        )
                    })
                }
            </tbody>
        </table>
        <button onClick={console.log()}>Добавить строку</button>
    </div>
  )
}

export default BasicTable;
