import React, { useState } from "react";

import classes from "./MyTable.module.css";

import '../../../utils/editTables'

const MyTable = ({ fulldata }) => {
  if (!fulldata || fulldata.length === 0) return <p>Нет данных</p>;

  const EditableTable = ({ initialData }) => {
	const [data, setData] = useState(initialData);
  
	const addRow = () => {
	  const newRow = Object.keys(data[0] || {}).reduce((acc, key) => {
		acc[key] = "";
		return acc;
	  }, {});
	  setData([...data, newRow]);
	};
  
	const handleChange = (rowIndex, key, value) => {
	  const updatedData = data.map((row, index) =>
		index === rowIndex ? { ...row, [key]: value } : row
	  );
	  setData(updatedData);
	};
  
	return (
	  <div className={classes.container}>
		<div className={classes.table}>
		  <div className={classes.table__row}>
			{Object.keys(data[0] || {}).map((key, index) => (
			  <div
				key={index}
				className={`${classes.table__cell} ${classes.table__header}`}
			  >
				{key}
			  </div>
			))}
		  </div>
		  {data.map((row, rowIndex) => (
			<div key={rowIndex} className={classes.table__row}>
			  {Object.keys(row).map((key, index) => (
				<div key={index} className={classes.table__cell}>
				  <input
					type="text"
					value={row[key]}
					onChange={(e) => handleChange(rowIndex, key, e.target.value)}
				  />
				</div>
			  ))}
			</div>
		  ))}
		</div>
		<button onClick={addRow} className={classes.addButton}>Добавить строку</button>
	  </div>
	);
  };
}
  
  export default MyTable;
