import React, { useMemo, useState, useEffect, useImperativeHandle, forwardRef } from 'react';
import { useTable, useSortBy } from 'react-table';
import { fetchData } from '../../../utils/fetchData';
import { Pencil } from 'lucide-react';
import classes from './MyTable.module.css';

const BasicTable = forwardRef(({ columnlist, fulldata, onEditingChange }, ref) => {
    const currentTab = window.location.pathname.slice(1);
    const [data, setData] = useState(fulldata);
    const [editableCell, setEditableCell] = useState(null);
    const [previousValue, setPreviousValue] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const columns = useMemo(() => columnlist, []);
    const [, updateComponent] = useState(0);
    const [response, setResponse] = useState(null);
    const [fetchError, setFetchError] = useState(null);
    const [editableRowIndex, setEditableRowIndex] = useState(null);

    useEffect(() => {
        onEditingChange?.(isEditing);
    }, [isEditing, onEditingChange]);

    const tableInstance = useTable({ columns, data }, useSortBy);
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

    console.log(isEditing)

    const handleEditClick = (rowIndex, columnId, value) => {
        setEditableCell({ rowIndex, columnId });
        setPreviousValue(value);
        setIsEditing(true);
    };

    const handleInputChange = (rowIndex, columnId, value) => {
        const newData = [...data];
        newData[rowIndex][columnId] = value;
        setData(newData);
    };

    const handleBlur = () => {
        setEditableCell(null);
        setIsEditing(false);
    };

    const sendData = () => {
        if (editableCell) {
            const { rowIndex, columnId } = editableCell;
            const rowData = { ...data[rowIndex] };
            rowData[columnId] = rowData[columnId] || '-';
            fetchData(currentTab, 'post', rowData, setResponse, setFetchError);
            updateComponent((prev) => prev + 1);
            setEditableCell(null);
            setIsEditing(false);
        }
    };

    const addRow = () => {
        const lastArray = data.length > 0 ? data.at(-1) : { id: 0 };
        const newRow = {
            id: lastArray.id + 1,
        };
        setData([...data, newRow]);
        setEditableRowIndex(data.length);
    };

    const cancelEdit = () => {
        if (editableRowIndex !== null) {
            const lastRow = data[editableRowIndex];
            setData(prevData => prevData.slice(0, -1));
            setEditableRowIndex(null);
        }
    };

    useImperativeHandle(ref, () => ({
        sendData,
        addRow,
        cancelEdit
    }));
    
    useEffect(() => {
        setData(fulldata);
    }, [fulldata]);

    return (
        <div className={classes.container}>
            <table className={classes.table} {...getTableProps()}>
                <thead>
                    {headerGroups.map((headerGroup, index) => (
                        <tr {...headerGroup.getHeaderGroupProps()} key={headerGroup.getHeaderGroupProps().key || index} className={classes.table__row}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()} key={column.getHeaderProps().key || column.id} className={classes.table__cell}>
                                    {column.render('Header')}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map((row, rowIndex) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()} key={row.getRowProps().key || rowIndex} className={classes.table__row}>
                                {row.cells.map((cell) => (
                                    <td {...cell.getCellProps()} key={cell.column.id} className={classes.table__cell}>
                                    {rowIndex === editableRowIndex ? (
                                        cell.column.id === 'id' ? (
                                            <div>{data[rowIndex][cell.column.id]}</div> // id нельзя редактировать
                                        ) : (
                                            <input
                                                className={classes.input}
                                                value={data[rowIndex][cell.column.id] || ''}
                                                onChange={(e) => handleInputChange(rowIndex, cell.column.id, e.target.value)}
                                                onBlur={handleBlur}
                                            />
                                        )
                                    ) : editableCell?.rowIndex === rowIndex && editableCell?.columnId === cell.column.id ? (
                                        <input
                                            className={classes.input}
                                            value={data[rowIndex][cell.column.id] || ''}
                                            onChange={(e) => handleInputChange(rowIndex, cell.column.id, e.target.value)}
                                            onBlur={handleBlur}
                                            autoFocus
                                        />
                                    ) : (
                                        <div className={classes.cellContent}>
                                            {cell.render('Cell')}
                                            {(rowIndex === editableRowIndex || 
                                              cell.column.id == 'Статус' || 
                                              cell.column.id.startsWith('Сотрудник') || 
                                              cell.column.id == 'Адрес' || 
                                              cell.column.id == 'Широта' || 
                                              cell.column.id == 'Долгота' || 
                                              cell.column.id == 'Город' || 
                                              cell.column.id == 'Индекс') && (
                                                <Pencil
                                                    className={classes.editIcon}
                                                    size={16}
                                                    onClick={() => handleEditClick(rowIndex, cell.column.id, data[rowIndex][cell.column.id])}
                                                />
                                            )}
                                        </div>
                                    )}
                                </td>                                                                
                                ))}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            {fetchError && <div>{fetchError}</div>}
        </div>
    );
});

export { BasicTable };
