import React, { useMemo, useState, useEffect } from 'react';
import { useTable } from 'react-table';
import { COLUMNS } from './Columns';
import classes from './MyTable.module.css';

function BasicTable({ fulldata }) {
    const [data, setData] = useState(fulldata); // Данные таблицы
    const [editableRowIndex, setEditableRowIndex] = useState(null); // Редактируемая строка

    const columns = useMemo(() => COLUMNS, []); 

    const tableInstance = useTable({
        columns,
        data
    });

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

    // Функция добавления новой строки
    const addRow = () => {
        const newRow = {
            id: data.length + 1,
            name: '',
            age: '',
        };
        setData([...data, newRow]);
        setEditableRowIndex(data.length);
    };

    // Обработчик изменения значений в новой строке
    const handleInputChange = (rowIndex, columnId, value) => {
        if (rowIndex === editableRowIndex) {
            const newData = [...data];
            newData[rowIndex][columnId] = value;
            setData(newData);
        }
    };

    // Сброс состояния при переключении вкладки
    useEffect(() => {
        const handleVisibilityChange = () => {
            if (document.hidden) {
                setData(fulldata); // Сброс данных
                setEditableRowIndex(null); // Сброс редактируемой строки
            }
        };

        document.addEventListener("visibilitychange", handleVisibilityChange);
        return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
    }, [fulldata]);

    return (
        <div className={classes.container}>
            <button className={classes.addButton} onClick={addRow}>Добавить строку</button>
            <table className={classes.table} {...getTableProps()}>
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr className={classes.table__row} {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th className={classes.table__cell} {...column.getHeaderProps()}>
                                    {column.render('Header')}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map((row, rowIndex) => {
                        prepareRow(row);
                        return (
                            <tr className={classes.table__row} {...row.getRowProps()}>
                                {row.cells.map(cell => (
                                    <td className={classes.table__cell} {...cell.getCellProps()}>
                                        {rowIndex === editableRowIndex && cell.column.id !== 'id' ? (
                                            <input
                                                className={classes.input}
                                                value={cell.value || ''}
                                                onChange={(e) => handleInputChange(rowIndex, cell.column.id, e.target.value)}
                                            />
                                        ) : (
                                            cell.render('Cell')
                                        )}
                                    </td>
                                ))}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    );
}

export default BasicTable;
