import React, { useState } from "react";
import classes from "./MyTable.module.css";

const MyTable = ({ fulldata }) => {
  const [data, setData] = useState(fulldata || []);

  const addEmptyRow = () => {
    if (data.length === 0) return;
    const emptyRow = Object.keys(data[0]).reduce((acc, key) => {
      acc[key] = "";
      return acc;
    }, {});
    setData([...data, emptyRow]);
  };

  const handleChange = (rowIndex, key, value) => {
    const newData = [...data];
    newData[rowIndex][key] = value;
    setData(newData);
  };

  if (!data || data.length === 0) return <p>Нет данных</p>;

  return (
    <div className={classes.container}>
      <button onClick={addEmptyRow} className={classes.addButton}>Добавить строку</button>
      <div className={classes.table}>
        <div className={classes.table__row}>
          {Object.keys(data[0] || {}).map((key, index) => (
            <div key={index} className={`${classes.table__cell} ${classes.table__header}`}>
              {key}
            </div>
          ))}
        </div>
        {data.map((row, rowIndex) => (
          <div key={rowIndex} className={classes.table__row}>
            {Object.entries(row).map(([key, value], index) => (
              <div key={index} className={classes.table__cell}>
                <input
                  type="text"
                  value={value}
                  onChange={(e) => handleChange(rowIndex, key, e.target.value)}
                />
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyTable;
