import React, { useEffect, useState, useRef } from "react";
import { fetchData } from "../../utils/fetchData";

import Loader from "../../components/UI/Loader/Loader";

import classes from "../Page.module.css";
// import MyTable from "../../components/UI/Table/MyTable";
import MyButton from "../../components/UI/Button/MyButton";
import BasicTable from "../../components/UI/Table/BasicTable";

const Assets = () => {
	const tab = window.location.pathname.split("/assets/")[1];
	const [data, setData] = useState(null);
	const [values, setValues] = useState(null);

	useEffect(() => {
		fetchData("assets", "get", null, setData);
	}, []);

	const handleChange = (event, field) => {
		setValues((prevValues) => ({
			...prevValues,
			[field]: event.target.value,
		}));
	};

	const send = () => {
		// fetchData("auth/login", values, );
	};

	// const keys = data ? Object.valu(data) : [];

	const tableRef = useRef();

	return (
		<div className={classes.main}>
			{data ? (
				<>
					<BasicTable ref={tableRef} fulldata={data[tab]} />
					<div>
						<MyButton style={{ marginTop: '20px', width: '250px'}} text="Создать запись" onClick={() => tableRef.current?.addRow()} />
						<MyButton style={{ marginTop: '20px', width: '250px'}} text="Создать запись" />
					</div>
				</>
			) : (
				<div style={{ margin: "auto" }}>
					<Loader />
				</div>
			)}
			{/* {data ? keys.map((key) => <div key={key}>{key}</div>) : <Loader />} */}
		</div>
	);
};

export default Assets;
