import React, { useEffect, useState, useRef, useCallback } from "react";
import { fetchData } from "../../utils/fetchData";
import Loader from "../../components/UI/Loader/Loader";
import classes from "../Page.module.css";
import MyButton from "../../components/UI/Button/MyButton";
import { BasicTable } from "../../components/UI/Table/BasicTable";
import MySubmitButton from "../../components/UI/SubmitButton/MySubmitButton";
import { COLUMNS } from "../../components/UI/Table/Columns";

const Assets = () => {
    const tab = window.location.pathname.split("/assets/")[1];
    const [data, setData] = useState(null);
    const [values, setValues] = useState();
    const [isVisible, setVisible] = useState(false);
    const [isEditingWithPencil, setIsEditingWithPencil] = useState(false);
    const tableRef = useRef(null);
    const fileInputRef = useRef(null);

    useEffect(() => {
        setData(null);
        fetchData("assets", "get", null, (fetchedData) => {
            setData(fetchedData);
        });
    }, [tab]);

    const toggleVisible = useCallback(() => {
        setVisible((prev) => !prev);
    }, []);

    const handleTableEditingChange = useCallback((editing) => {
        setIsEditingWithPencil(editing);
    }, []);

    let buttonText = isVisible ? "Отмена" : "Создать запись";

    return (
        <div className={classes.main}>
            {data ? (
                <>
                    <BasicTable
                        key={`${tab}-${values}`}
                        columnlist={COLUMNS}
                        ref={tableRef}
                        fulldata={data[tab]}
                        onEditingChange={handleTableEditingChange}
                    />
                    <div style={{ display: 'flex' }}>
                        <MyButton
                            style={{ marginTop: '20px', marginRight: '20px', width: '250px' }}
                            onClick={() => {
                                if (isEditingWithPencil) {
                                    tableRef.current?.cancelEdit();
                                    setIsEditingWithPencil(false);
                                } else {
                                    tableRef.current?.addRow();
                                    setIsEditingWithPencil(true);
                                }
                                toggleVisible();
                            }}
                            text={buttonText}
                            isActive={isVisible}
                        />
                        <MySubmitButton
                            isActive={isVisible}
                            style={{ marginTop: '20px', marginRight: '20px', width: '250px' }}
                            text="Подтвердить" 
                            onClick={() => {
                                tableRef.current?.sendData();
                                setVisible(false);
                                setIsEditingWithPencil(false);
                            }}
                        />
                    </div>
                </>
            ) : (
                <div style={{ margin: "auto" }}>
                    <Loader />
                </div>
            )}
        </div>
    );
};

export default Assets;
